import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'

Vue.use(Vuex)

const store = new Vuex.Store({
    state: {
      users: [] 
    },
    actions: {
      GET_USERS_FROM_API({commit}){
        return axios('http://localhost:8083/users')
      }
    },
    mutations: {},
    getters: {}
})

export default store;
